pragma solidity ^0.5.0;

contract Patient {
    bytes32 public name;

    constructor( bytes32 _name) public {
        name = _name;
    }
}

contract PatientFactory {
    Patient[] public users;
    function createUser(bytes32 _name) external {
        Patient user = new Patient(_name);

        emit UserCreated(user, users.length);

        users.push(user);
    }

    event UserCreated(Patient user, uint256 index);
}
